import { useState } from "react";

import axios from "axios";

const Api = () => {
  const [data, setData] = useState(null);
  const [saerchCity, setsSaerchCity] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=${saerchCity}&appid=0cb2908b500b15dcc33edef5b4168a02&units=metric`
      )
      .then((res) => {
        setData(res.data);
        // setCity(res.data.name);
        console.log(res.data);
      })
      .catch(function (error) {
        console.log("Show error notification!");
        // return Promise.reject(error);
        setData("");
        setError(true);
        return null;
      });

    setLoading(false);
    setsSaerchCity("");
  };

  return [data, saerchCity, setsSaerchCity, loading, error];
};

export default Api;
