import { useState } from "react";

import axios from "axios";

function Weather() {
  const [data, setData] = useState("");
  const [city, setCity] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/weather?q=cairo&appid=0cb2908b500b15dcc33edef5b4168a02&units=metric`
      )
      .then((res) => {
        setData(...data, res.data.main);
        setCity(res.data.name);
      });
    setLoading(false);
  };

  return (
    <>
      <div className="main">Weather App</div>
      <div className="body">
        <button className="button" onClick={fetchData}>
          Fetch Data
        </button>
        {loading && <h1>Loading....</h1>}
        {data ? (
          <div>
            <h3>City: {city}</h3>
            <h3>Temp: {data.temp}</h3>
          </div>
        ) : (
          <p>Inter a Valid City</p>
        )}
      </div>
    </>
  );
}

export default Weather;
