import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import rainy from "../assets/rainy.png";
import GetData from "../hooks/getData";

const Weather = () => {
  const [data, saerchCity, setsSaerchCity, loading, error, fetchData] =
    GetData();
  return (
    <Container fluid>
      <Row className="justify-content-center py-5 ">
        <Col className="text-center" sm={12} style={{ color: "#B3C8CF" }}>
          <h1>Weather App</h1>
        </Col>
      </Row>
      <Row className="justify-content-center">
        <Col sm={10}>
          <Card>
            <Card.Img variant="top" src={rainy} style={{ height: "24rem" }} />
            <Card.Body>
              <div className="main">
                <div>
                  <input
                    type="text"
                    value={saerchCity}
                    placeholder="Enter city/town..."
                    onChange={(e) => setsSaerchCity(e.target.value)}
                  />

                  <button onClick={fetchData}>Search</button>
                </div>
              </div>
              <Card.Title>Result Of :</Card.Title>
              <Card.Text>
                Some quick example text to build on the card title and make up
                the bulk of the card's content.
              </Card.Text>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Weather;
