import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";
import rainy from "../assets/rainy.png";
import GetData from "../hooks/getData";

const Weather = () => {
  const [data, saerchCity, setsSaerchCity, loading, error, fetchData] =
    GetData();
  return (
    <Container fluid>
      <Row className="justify-content-center py-5 ">
        {/* <Image src={rainy} fluid />; */}
        <Col sm={8}>
          <div
            className="card text-center bg-light text-dark"
            // style={{ width: "48rem" }}
          >
            <div className="card-body">
              {/* <span className="badge bg-secondary">New</span> */}
              <h3 className="card-title">Weather App </h3>
              <div className="main">
                <div>
                  <input
                    type="text"
                    value={saerchCity}
                    placeholder="Enter city/town..."
                    onChange={(e) => setsSaerchCity(e.target.value)}
                  />

                  <button onClick={fetchData}>Search</button>
                </div>
              </div>

              {/* <h3 className="card-title">{data.name}</h3> */}

              <p className="card-text">Some quick example text</p>
            </div>
          </div>
        </Col>
        <Col sm={4}>
          <div
            className="card text-center bg-light text-dark"
            // style={{ width: "24rem" }}
          >
            <img src={rainy} className="card-img-top" alt="..."></img>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Weather;
